// Indexes
courseSchema.index({ category: 1, status: 1 });
courseSchema.index({ slug: 1 });
courseSchema.index({ instructor: 1 });
courseSchema.index({ 'price.discounted': 1 });
courseSchema.index({ 'rating.average': -1 });
courseSchema.index({ enrollmentCount: -1 });
courseSchema.index({ createdAt: -1 });

// Virtual for discounted price
courseSchema.virtual('currentPrice').get(function() {
  return this.price.discounted || this.price.original;
});

// Virtual for discount percentage
courseSchema.virtual('discountPercentage').get(function() {
  if (this.price.discounted && this.price.discounted < this.price.original) {
    return Math.round(((this.price.original - this.price.discounted) / this.price.original) * 100);
  }
  return 0;
});

// Virtual for total lessons count
courseSchema.virtual('totalLessons').get(function() {
  return this.lessons.length;
});

// Virtual for total video duration
courseSchema.virtual('totalVideoDuration').get(function() {
  return this.lessons.reduce((total, lesson) => total + lesson.videoDuration, 0);
});

// Pre-save middleware to generate slug
courseSchema.pre('save', function(next) {
  if (this.isModified('title')) {
    this.slug = this.title
      .toLowerCase()
      .replace(/[^a-zA-Z0-9 ]/g, '')
      .replace(/\s+/g, '-');
  }
  next();
});

// Method to calculate average rating
courseSchema.methods.calculateAverageRating = function() {
  if (this.reviews.length === 0) {
    this.rating.average = 0;
    this.rating.count = 0;
    return;
  }

  const totalRating = this.reviews.reduce((sum, review) => sum + review.rating, 0);
  this.rating.average = Math.round((totalRating / this.reviews.length) * 10) / 10;
  this.rating.count = this.reviews.length;
};

// Method to add review
courseSchema.methods.addReview = function(userId, rating, comment) {
  // Remove existing review from same user
  this.reviews = this.reviews.filter(review => review.user.toString() !== userId.toString());

  // Add new review
  this.reviews.push({
    user: userId,
    rating,
    comment
  });

  this.calculateAverageRating();
  return this.save();
};

// Method to check if user can access course
courseSchema.methods.canUserAccess = function(user) {
  if (!user) return false;

  // Admin and instructor can always access
  if (user.role === 'admin' || user._id.toString() === this.instructor.toString()) {
    return true;
  }

  // Check if user is enrolled
  return user.enrolledCourses.some(
    enrollment => enrollment.course.toString() === this._id.toString()
  );
};

// Static method to get featured courses
courseSchema.statics.getFeatured = function(limit = 6) {
  return this.find({ 
    status: 'published', 
    isActive: true, 
    isFeatured: true 
  })
  .populate('instructor', 'name avatar')
  .sort({ 'rating.average': -1, enrollmentCount: -1 })
  .limit(limit);
};

// Static method to get courses by category
courseSchema.statics.getByCategory = function(category, limit = 10) {
  return this.find({ 
    category, 
    status: 'published', 
    isActive: true 
  })
  .populate('instructor', 'name avatar')
  .sort({ 'rating.average': -1, enrollmentCount: -1 })
  .limit(limit);
};

